// Example IG Selection Logic
function determineImplementationGroup(org) {
  const criteria = {
    dataComplexity: org.dataComplexity,  // low, medium, high
    resourceLevel: org.resources,         // limited, moderate, substantial
    staffExpertise: org.expertise         // basic, intermediate, advanced
  };

  if (criteria.resourceLevel === 'limited') {
    return 'IG1';
  } else if (criteria.resourceLevel === 'moderate') {
    return 'IG2';
  } else {
    return 'IG3';
  }
}
